import React from 'react';
import PopupWithForm from './PopupWithForm';

import { CurrentUserContext } from '../contexts/CurrentUserContext';

export default function EditAvatarPopup({isOpen,onClose,onUpdateAvatar}){
  const currentUser = React.useContext(CurrentUserContext);
  const [value, setValue] = React.useState('');

  React.useEffect(() => {
    setValue({
      avatar: currentUser.avatar,
    });
  }, [currentUser]);

  function handleChangeAvatar(e) {
    setValue(value);
  }
  function handleSubmitAvatar(e){
    e.preventDefault();
    console.log(value)
    onUpdateAvatar(value);
  }

  return(
    <PopupWithForm
    name = "avatar"
    title="Изменить аватар"
    buttonText="Сохранить"
    isOpen={isOpen}
    onClose ={onClose}
    onSubmit={handleSubmitAvatar}
     >
      <label className="form__field">
          <input className="form__input form__input_type_avatar" type="url" placeholder="Ссылка на картинку" name="avatar" id="avatar" required onChange={handleChangeAvatar}/>
          <span className="form__input-error" id="avatar-error"> </span>
        </label>

      </PopupWithForm>
  )
}
