import React from "react";
import headerLogo from "../images/logo.svg";
import { useNavigate, useState } from "react-router-dom";

function Register() {
  const navigate = useNavigate();

  const goToLogin = () => {
    navigate("/sign-in");
  };

  const [formValue, setFormValue] = useState({
    email: "",
    password: "",
  });

  const [errorMessage, setErrorMessage] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormValue({
      ...formValue,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  };
  return (
    <>
      <div className="header">
        <img className="header__logo" alt="Логотип" src={headerLogo} />
        <button className="header__button-exit" onClick={goToLogin}>
          Войти
        </button>
      </div>

      <form className="registration" onSubmit={handleSubmit}>
        <h2 className="registration__title">Регистрация</h2>
        <input className="registration__input" placeholder="Email"></input>
        <p className="register__error">
        {errorMessage}</p>
        <input className="registration__input" placeholder="Password"></input>
        <p className="register__error">
        {errorMessage}</p>
        <button className="registration__submit">Зарегестрироваться</button>
        <button className="registration__link" onClick={goToLogin}>
          {" "}
          Уже зарегистрированны? Войти
        </button>
      </form>
    </>
  );
}

export default Register;
