import React from "react";
import headerLogo from "../images/logo.svg";
import { useNavigate } from "react-router-dom";

function Login(){
  const navigate = useNavigate()

  const goToRegistration = () =>{
    navigate ('/sign-up');}

    const [formValue, setFormValue] = React.useState({
      username: '',
      password: ''
    });

    const handleChange = (e) => {
      const { name, value } = e.target;
      setFormValue({
        ...formValue,
        [name]: value
      });
    }

    const { username, password } = formValue;

    const handleSubmit = (e)=>{
      e.preventDefault();
    }

   return(
    <>
    <div className="header">
      <img className="header__logo" alt="Логотип" src={headerLogo} />
      <button className="header__button-registration" onClick={goToRegistration}>Регистрация</button>
    </div>
          <form className="registration" onSubmit={handleSubmit}>
          <h2 className="registration__title">Вход</h2>
          <input className="registration__input" placeholder="Email" placeholder="Email"
          onChange={handleChange}
          id="email"
          name="email"
          type="email"
          autoComplete="email"
          value={formValue.email}></input>
          <input className="registration__input" placeholder="Password" placeholder="Password"
          id="password"
          name="password"
          type="password"
          autoComplete="new-password"
          value={formValue.password}></input>
          <button className="registration__submit">Войти</button>
          </form>
</>
  )
}
export default Login;
