import React, { useState, useContext, useEffect } from 'react';
import PopupWithForm from './PopupWithForm';

export default function ConfirmationPopup({isOpen,onClose,onConfirm,isLoading}){

  function handleSubmit(evt) {
    evt.preventDefault();

    onConfirm();
  }
  return(
    <PopupWithForm
    name = "trash"
    title="Вы уверены?"
    buttonText={isLoading ? 'Удаление...' : 'Удалить'}
    isOpen={isOpen}
    onClose ={onClose}
    onSubmit={handleSubmit}
     />
  )

}
