import React from 'react';
import Card from './Card.js'
import { CurrentUserContext } from '../contexts/CurrentUserContext';


function Main (props){
  const currentUser = useContext(CurrentUserContext);

  return(
<main className="content">
  <section className="profile">
   <button className="profile__button-avatar" type="button" onClick={props.onEditAvatar}>
     <img className="profile__avatar" alt="фото пользователя" src= {props.currentUser.avatar}  />
   </button>
     <div className="profile__info">
        <h1 className="profile__info-title">{props.currentUser.name}</h1>
        <button className="profile__edit-button" type ="button" onClick={props.onEditProfile} ></button>
        <p className="profile__info-subtitle">{props.currentUser.about}</p>
     </div>
     <button className="profile__add-button" type ="button" onClick={props.onAddPlace}></button>
  </section>

  <section className="elements">
     <ul className="element">
      {props.cards.map((card)=>{
        return <Card
        card = {card}
        key = {card._id}
        onOpenImage = {props.onOpenImage}
        />
})}

     </ul>
  </section>
</main>)
}

export default Main
