import '../index.css';
import Header from './Header.js'
import Main from './Main.js'
import Footer from './Footer.js'
import { useState } from 'react';
import PopupWithForm from './PopupWithForm.js'
//import ImagePopup from './ImagePopup.js'
//import Card from './Card.js'

function App() {
  const  [isAvatarPopupOpen,setAvatarPopupOpen] = useState(false)
  const  [isProfilePopupOpen,setProfilePopupOpen] = useState(false)
  const  [isAddCardPopupOpen,setAddCardPopupOpen] = useState(false)

  const handleEditAvatarClick =()=> {
    setAvatarPopupOpen(true)
    }
    const handleEditProfileClick = () => {
      setProfilePopupOpen(true)
    }
    const handleAddCardClick =() => {
      setAddCardPopupOpen(true)
    }
  return (

    <div className="App">
      <div className="page">
        <Header />
        <Main
        onEditProfile={handleEditProfileClick}
        onAddPlace={handleAddCardClick}
        onEditAvatar={handleEditAvatarClick} />
        <Footer />
        <PopupWithForm
        name = "avatar"
        title="Изменить аватар"
        buttonText="Сохранить"
        isOpen={isAvatarPopupOpen}
         >

          </PopupWithForm>

         <PopupWithForm
        name = "card"
        title="Новое место"
        buttonText="Сохранить"
        isOpen={isAddCardPopupOpen}
         />

         <PopupWithForm
        name = "profile"
        title="Редактировать профиль"
        buttonText="Сохранить"
        isOpen={isProfilePopupOpen}
         >
          <label className="form__field">
          <input className="form__input form__input_type_name"
            type="text"
            name="name"
            placeholder="Имя"
            id="Username"
            minlength="2"
            maxlength="40"
            required />
          <span className="form__input-error" id="Username-error"> </span>
          </label>

          <label className="form__field">
          <input className="form__input form__input_type_about"
              type="text"
              placeholder="Описание профиля"
              name="about" id="about"
              minlength="2"
              maxlength="200"
              required />
          <span className="form__input-error" id="about-error"> </span>
          </label>

          </PopupWithForm>

   </div>
    </div>
  );
}

export default App;
